import React, { useState } from 'react';
import './EmailVerification.css';

const EmailVerification = () => {
  const [email, setEmail] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [showVerificationCode, setShowVerificationCode] = useState(false);
  const [status, setStatus] = useState({ message: '', type: '' });
  const [isLoading, setIsLoading] = useState(false);

  const isValidEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const showStatus = (message, type) => {
    setStatus({ message, type });
    setTimeout(() => {
      setStatus({ message: '', type: '' });
    }, 5000);
  };

  const handleSendVerification = async () => {
    if (!email) {
      showStatus('Please enter an email address', 'error');
      return;
    }

    if (!isValidEmail(email)) {
      showStatus('Please enter a valid email address', 'error');
      return;
    }

    setIsLoading(true);

    // Simulate API call
    setTimeout(() => {
      showStatus('Verification email sent! Check your inbox.', 'success');
      setShowVerificationCode(true);
      setIsLoading(false);
    }, 2000);
  };

  const handleResendVerification = () => {
    if (!email) {
      showStatus('Please enter an email address first', 'error');
      return;
    }

    showStatus('Verification code resent! Check your inbox.', 'success');
  };

  const handleVerifyCode = async () => {
    if (!verificationCode || verificationCode.length !== 6) {
      showStatus('Please enter a valid 6-digit code', 'error');
      return;
    }

    setIsLoading(true);

    // Simulate verification process
    setTimeout(() => {
      showStatus('Email verified successfully! You can now use EvenX.', 'success');

      // Redirect to app or show success message
      setTimeout(() => {
        // You can redirect to your mobile app here
        showStatus('Redirecting to EvenX app...', 'success');

        // Example deep linking (update with your app's scheme)
        // window.location.href = 'evenx://verification-success';
      }, 2000);

      setIsLoading(false);
    }, 1500);
  };

  const handleEmailChange = (e) => {
    const value = e.target.value;
    setEmail(value);

    // Enable/disable send button based on email validity
    const sendBtn = document.querySelector('button[onClick="handleSendVerification()"]');
    if (sendBtn) {
      sendBtn.disabled = !isValidEmail(value);
    }
  };

  const handleCodeChange = (e) => {
    const value = e.target.value.replace(/[^0-9]/g, '').slice(0, 6);
    setVerificationCode(value);
  };

  return (
    <section id="verification" className="verification">
      <div className="container">
        <div className="verification-content">
          <h2>Email Verification</h2>
          <p>Please verify your email address to continue using EvenX</p>

          <div className="verification-form">
            <div className="form-group">
              <label htmlFor="email">Email Address</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={handleEmailChange}
                placeholder="Enter your email address"
                required
              />
            </div>

            <div className="verification-options">
              <button
                className="btn btn-primary"
                onClick={handleSendVerification}
                disabled={isLoading || !isValidEmail(email)}
              >
                {isLoading ? (
                  <i className="fas fa-spinner fa-spin"></i>
                ) : (
                  <i className="fas fa-paper-plane"></i>
                )}
                {isLoading ? 'Sending...' : 'Send Verification Email'}
              </button>

              <button
                className="btn btn-secondary"
                onClick={handleResendVerification}
                disabled={!email}
              >
                <i className="fas fa-redo"></i>
                Resend Code
              </button>
            </div>

            {showVerificationCode && (
              <div className="verification-code">
                <div className="form-group">
                  <label htmlFor="code">Verification Code</label>
                  <input
                    type="text"
                    id="code"
                    value={verificationCode}
                    onChange={handleCodeChange}
                    placeholder="Enter 6-digit code"
                    maxLength="6"
                  />
                </div>
                <button
                  className="btn btn-success"
                  onClick={handleVerifyCode}
                  disabled={isLoading || verificationCode.length !== 6}
                >
                  {isLoading ? (
                    <i className="fas fa-spinner fa-spin"></i>
                  ) : (
                    <i className="fas fa-check"></i>
                  )}
                  {isLoading ? 'Verifying...' : 'Verify Code'}
                </button>
              </div>
            )}
          </div>

          {status.message && (
            <div className={`verification-status ${status.type}`}>
              {status.message}
            </div>
          )}
        </div>
      </div>
    </section>
  );
};

export default EmailVerification;
